import React, { useEffect, useState } from "react";
import Card from "./Card";
import Pokeinfo from "./Pokeinfo";
import axios from "axios";

const Main = () => {
    const [pokemonData, setPokemonData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [apiUrl, setApiUrl] = useState("https://pokeapi.co/api/v2/pokemon/?limit=20");
    const [nextApiUrl, setNextApiUrl] = useState();
    const [prevApiUrl, setPrevApiUrl] = useState();
    const [selectedPokemon, setSelectedPokemon] = useState();

    const fetchData = async () => {
        setLoading(true);
        const response = await axios.get(apiUrl);
        setNextApiUrl(response.data.next);
        setPrevApiUrl(response.data.previous);
        getPokemonData(response.data.results);
        setLoading(false);
        console.log(pokemonData);
    };

    const getPokemonData = async (pokemonList) => {
        const pokemonDetails = await Promise.all(
            pokemonList.map(async (item) => {
                const result = await axios.get(item.url);
                return result.data;
            })
        );

        setPokemonData(pokemonDetails.sort((a, b) => (a.id > b.id ? 1 : -1)));
    };

    useEffect(() => {
        fetchData();
    }, [apiUrl]);

    return (
        <>
            <div className="container">
                <div className="left-content">
                    <Card pokemon={pokemonData} loading={loading} infoPokemon={(poke) => setSelectedPokemon(poke)} />

                    <div className="btn-group">
                        <button
                            onClick={() => {
                                setPokemonData([]);
                                setApiUrl(prevApiUrl);
                            }}
                        >
                            Previous
                        </button>
                        <button
                            onClick={() => {
                                setPokemonData([]);
                                setApiUrl(nextApiUrl);
                            }}
                        >
                            Next
                        </button>
                    </div>
                </div>
                <div className="right-content">
                    <Pokeinfo data={selectedPokemon} />
                </div>
            </div>
        </>
    );
};

export default Main;
